import React, {useState, useEffect} from "react"

const Nwe  = () => {
    const randomNumber = (x) => {
        let result = Math.floor(Math.random()*x)
        return result
    }
    const [username, setUsername] = useState("");
    const [avatarUrl, setAvatarUrl] = useState(`https://api.adorable.io/avatars/face/${randomNumber(1000)}`)
    const [newUser, setNewUser] = useState(true)
    const handleChangeUsername = (event) => {
        let newUsername = event.target.value
        setUsername(newUsername)
    }

    const getNewAvatar = ()=>{
        setAvatarUrl(`https://api.adorable.io/avatars/face/${randomNumber(1000)}`)
    } 


    return(
        <div>
            <h1>Welcome to Trivia Night</h1>
            <input value={username}  onChange={handleChangeUsername} placeholder={"insert name"}/>
            <img className="Avatar" src={avatarUrl} alt= "avatar" />
            <button onClick={getNewAvatar}>Get another avatar</button>
        </div>
    )
}

export default LandingPage
