import React, {useState, useEffect, useRef} from "react"
import avatar from "../../images/51.png"



const Signup  = () => {
    const randomNumber = (x) => {
        let result = Math.floor(Math.random()*x)
        return result
    }
    const nameInput = useRef()
    const passwordInput = useRef()

    const [username, setUsername] = useState("");
    const [avatarUrl, setAvatarUrl] = useState(avatar)
    const handleChangeUsername = (event) => {

        let newUsername = event.target.value
        setUsername(newUsername)
    }

    const getNewAvatar = ()=>{
        setAvatarUrl(`https://api.adorable.io/avatars/face/${randomNumber(1000)}`)
    } 


    return(
        <div>
            <h1>Welcome to Trivia Night</h1>
            <input value={username}  name ={nameInput} onChange={handleChangeUsername} placeholder={"insert name"}/>
            <input value={username} name = {passwordInput} onChange={handleChangeUsername} type="password" placeholder={"password"}/>

            <img className="Avatar" src={avatarUrl} alt= "avatar" />
            <button onClick={getNewAvatar}>Get another avatar</button>
        </div>
    )
}

export default Signup
